        title: pdfh(html, '.stui-content__detail&&p,3&&Text').substring(0, 18) +
            '\n' +
            '\n' +
            pdfh(html,
                '.stui-content__detail&&p,4&&Text').substring(0, 18),
        desc: pdfh(html, '.stui-content__detail&&p,1&&Text'),
        img: pd(html,
            '.lazyload&&data-original') + '@Referer=',
        url: MY_URL,
        col_type: 'movie_1_vertical_pic_blur',
        extra: {
            gradient: true
        }
    });
} catch (e) {
    d.push({
        img: pd(html,
            '.lazyload&&data-original') + '@Referer=',
        url: MY_URL,
        col_type: 'movie_1_vertical_pic_blur',
        extra: {
            gradient: true
        }
    });
}
var 简介 = pdfh(html, '.detail-sketch&&Text')
d.push({
