            章节.reverse()
        }
        let cp = 章节.map((data, ssid) => {
            return {
                title: pdfh(data, 'a&&Text'),
                url: pd(data, 'a&&href') + lazy,
                col_type: 章节.length > 3 ? 'text_4' : 'text_2',
                extra: {
                    cls: MY_URL + "_选集",
                    id: pd(data, 'a&&href')
                }
            };
        });
        deleteItemByCls(MY_URL + "_选集");
