const page = MY_PAGE
if (getMyVar("url")) {
    true_url = true_url.replace(/v-type\/(\d+)\.html/, 'vod-show/$1-----------.html').replace(/---(\d*)\.html/, page + '---$1.html');
}
let c = getVar("freeok", "");
if (c != "") {
    setItem("cookie", c);
    clearVar("freeok");
}
//clearItem('cookie')
var cookie = getItem("cookie", "")

let headers = {
    "Cookie": cookie,
    "User-Agent": MOBILE_UA,   
};
//log(cookie)
let html = fetch(true_url, {
    headers: headers,
});
eval(JSON.parse(fetch('hiker://page/sm', {})).rule);
