                    ua: MOBILE_UA,
                    js: $.toString(() => {
                        function check() {
                            let c = fba.getCookie(location.href);
                            let nodes = document.querySelectorAll('.module-poster-item');

                            if (nodes && nodes.length > 0 && c) {
                                fba.putVar("freeok", c);
                                fba.log(c)
                                fba.parseLazyRule($$$().lazyRule(() => {
                                    back();
                                }));
                            } else {
                                setTimeout(check, 250);
                            }
                        }
                        check();
                    })
                }
            })
            d.push({
                title: "正在过安全验证，请稍候",
                url: $().lazyRule(() => {
                    back();
                    return 'toast://下拉刷新'
                }),
