            });
            setResult(d);
        })
    })
} else
var list = pdfa(html, '.module&&.module-poster-item');
for (var j in list) {
    d.push({
        title: pdfh(list[j], 'a&&title'),
        desc: pdfh(list[j], '.module-item-cover&&Text'),
        img: pd(list[j], '.lazyload&&data-original') + '@Referer=',
